import {Contract} from "near-api-js";
import * as nearAPI from "near-api-js";

async function init() {

    const ftContract = new Contract(window.walletConnection.account(), 'wrap.testnet', {
        changeMethods: ['ft_transfer_call', 'near_deposit'],
    });
    console.log(ftContract)

    const provider = new nearAPI.providers.JsonRpcProvider(window.nearConfig.nodeUrl);
    const argsBase64 = window.btoa(JSON.stringify({account_id:window.nearAccount.accountId}))
    const rawResult = await provider.query({
        request_type: "call_function",
        finality: "final",
        account_id: 'streaming-r-v2.dcversus.testnet',
        method_name: "get_account_outgoing_streams",
        args_base64: argsBase64,
    });
    console.log(JSON.parse(Buffer.from(rawResult.result).toString()))
