import getConfig from './config.js';
import {Contract} from "near-api-js";
import * as nearAPI from "near-api-js";

window.nearConfig = getConfig(process.env.VUE_APP_NEAR_ENV || 'testnet');

async function init() {

    // create a keyStore for signing transactions using the user's key
// which is located in the browser local storage after user logs in
    const keyStore = new nearAPI.keyStores.BrowserLocalStorageKeyStore();

// Initializing connection to the NEAR testnet
    const near = await nearAPI.connect({keyStore, ...window.nearConfig});

// Initialize wallet connection
    window.roketoConnection = new nearAPI.WalletConnection(near);

    const ftContract = new Contract(window.roketoConnection.account(), 'wrap.testnet', {
        changeMethods: ['ft_transfer_call', 'near_deposit'],
    });
    console.log(ftContract)
    await ftContract.near_deposit({}, 200000000000000, 1000000000000000000000000);
    await ftContract.ft_transfer_call({
        receiver_id: 'streaming-r-v2.dcversus.testnet',
        amount: '1000000000000000000000000', // 1 NEAR
        memo: 'Roketo transfer',
        msg: JSON.stringify({
            Create: {
                request: {
                    "owner_id": window.nearAccount.accountId,
                    "receiver_id": "rocketotest2.testnet",
                    "tokens_per_sec": 385802469135802469, // 1 month for 1 NEAR
                }
            }
        }),
    }, 200000000000000);

}

export {init}
